import { Component } from '@angular/core';

@Component({
  selector: 'app-first-component',
  templateUrl: './first-component.component.html',
  styleUrls: ['./first-component.component.scss','../../styles.scss']
})
export class FirstComponentComponent {
    counter = 0;

    setCounter(){
      this.counter = this.counter +1;
    }
}


<div>

    <h3>My website</h3>
    <div >
        <div >
          <a  class="bar-item button-style">Home</a>
          <a  class="bar-item button-style">Link 1</a>

        </div>
      </div>
      <div class="dropdown-hover"style="max-width:1200px;margin:auto">
        <button class="button-style" >Dropdown</button>
        <div  class="dropdown-content ">
          <a  class="bar-item button-style">Link 1</a>
          <a  class="bar-item button-style">Link 2</a>
          <a  class="bar-item button-style">Link 3</a>
        </div>
      </div>
{{counter}} <button (click)="setCounter()">Click Me</button>

</div>
