import { Component } from '@angular/core';

@Component({
  selector: 'app-first-component',
  templateUrl: './first-component.component.html',
  styleUrls: ['./first-component.component.scss']
})
export class FirstComponentComponent {
    counter = 0;

    setCounter(){
      this.counter = this.counter +1;
    }
}


<div>

    <h3>My website</h3>


    <div >
        <h2>Navigation Bar with Dropdown</h2>
        <p>Add a dropdown menu inside the navigation bar:</p>

        <div class="w3-bar w3-light-grey">
          <a  class="w3-bar-item w3-button">Home</a>
          <a  class="w3-bar-item w3-button">Link 1</a>
          <div class="dropdown">
            <button >Dropdown</button>
            <div class="dropdown-content">
              <a  >Link 1</a>
              <a  class="w3-bar-item w3-button">Link 2</a>
              <a  class="w3-bar-item w3-button">Link 3</a>
            </div>
          </div>
        </div>
      </div>

{{counter}} <button (click)="setCounter()">Click Me</button>

</div>
