import { Component } from '@angular/core';

@Component({
  selector: 'app-first-component',
  templateUrl: './first-component.component.html',
  styleUrls: ['./first-component.component.scss']
})
export class FirstComponentComponent {
    counter = 0;

    setCounter(){
      this.counter = this.counter +1;
    }
}


<div>

    <h3>My website</h3>


    <div >
        <h2>Navigation Bar with Dropdown</h2>
        <p>Add a dropdown menu inside the navigation bar:</p>

        <div class="w3-bar w3-light-grey">
          <a  class="bar-item button-style">Home</a>
          <a  class="bar-item button-style">Link 1</a>
          <div class="w3-dropdown-hover">
            <button class="w3-button" >Dropdown</button>
            <div  class="w3-dropdown-content bar-block ">
              <a  class="bar-item button-style">Link 1</a>
              <a  class="bar-item button-style">Link 2</a>
              <a  class="bar-item button-style">Link 3</a>
            </div>
          </div>
        </div>
      </div>

{{counter}} <button (click)="setCounter()">Click Me</button>

</div>
